import { useState } from "react";
import validator from 'validator';
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import { auth } from "./firebase-config";

export default function SignupPage() {
    const [registerFirstName, setRegisterFirstName] = useState("");
    const [registerLastName, setRegisterLastName] = useState("");
    const [registerEmail, setRegisterEmail] = useState("");
    const [registerPassword, setRegisterPassword] = useState("");
    const [registerConfirmPassword, setRegisterConfirmPassword] = useState("");
    const [error, setError] = useState("");

    const registerValidation = () => {
      if(registerEmail == "" || registerPassword == "" || registerConfirmPassword == "" || registerFirstName == "" || registerLastName == ""){
        setError('Please fill in all fields');
        return;
      }
      if(!validator.isEmail(registerEmail)){
        setError("Email is Not Correct");
        return;
      }
      if(!validator.isStrongPassword(registerPassword)){
        setError('Password is not strong enough');
          return;
      }
      if(registerPassword != registerConfirmPassword){
        setError("Passwords didn't match. Try again")
        return;
      }
      else{
        register();
      }
    }

    const register = async () => {
        try {
          const user = await createUserWithEmailAndPassword(
            auth,
            registerEmail,
            registerPassword
          );
          console.log("Successfully created User");
        } catch (error) {
          if (error.code === "auth/email-already-in-use") {
            setError("Email is already in use");
          } else {
            setError("An error occurred while registering. Please try again later.");
          }
          console.log(error.message);
        }
      };

      const container = {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      };

      const formContainer = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        backgroundColor: "#f8f8f8",
        padding: "20px",
        borderRadius: "10px",
      };

    return(
        <div className="Signup Page" style={container}>
          <div style={formContainer}>
            <h3> Register User </h3>
            <input
              placeholder="First Name..."
              onChange={(event) => {
                setRegisterFirstName(event.target.value);
              }}
            />
            <input
              placeholder="Last Name..."
              onChange={(event) => {
                setRegisterLastName(event.target.value);
              }}
            />
            <input
              placeholder="Email..."
              onChange={(event) => {
                setRegisterEmail(event.target.value);
              }}
            />
            <input
              placeholder="Password..."
              onChange={(event) => {
                setRegisterPassword(event.target.value);
              }}
            />
            <input
              placeholder="Confirm Password..."
              onChange={(event) => {
                setRegisterConfirmPassword(event.target.value);
              }}
            />
            <button onClick={registerValidation}> Create User</button>
            <p>{error}</p>

          </div>
      </div>

    )
}
