import { useState } from "react";
import { Link } from "react-router-dom";
import SignupPage from "./SignupPage";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
} from "firebase/auth";
import { auth } from "./firebase-config";
