import * as React from 'react';
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { doc, getDoc, updateDoc} from "firebase/firestore"
import { auth, db} from "../firebase-config";
import background from "../wallpaper.jpeg";
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { MobileDateTimePicker } from '@mui/x-date-pickers/MobileDateTimePicker';
import { DesktopDateTimePicker } from '@mui/x-date-pickers/DesktopDateTimePicker';

export default function CalculatorPage() {
    const userID = auth.currentUser.uid;
    const [activity, setActivity] = useState("");
    const [timeUnit, setTimeUnit] = useState("");
    const [time, setTime] = useState("");
    const [distanceUnit, setDistanceUnit] = useState("");
    const [distance, setDistance] = useState("");
    const [showResult, setShowResult] = useState(false); // state variable to show/hide result
    const [value, setValue] = React.useState(null);
    const [error, setError] = useState("");
    const [dateTimeError, setDateTimeError] = useState("");

    const displayValues = () => {
        if(activity === ""){
            setError("Please select an activity");
            return;
        }
        if(time === ""){
            setError("Please enter a time");
            return;
        }
        if(isNaN(time) || isNaN(distance)){
            setError("Please enter a valid time/distance");
            return;
        }
        if(timeUnit === ""){
            setError("Please select a time unit");
            return;
        }
        else{
            setError("");
            console.log(activity);
            console.log(time,timeUnit);
            console.log(distance, distanceUnit);
            setShowResult(true); // set state variable to show result
        }
    }

    const displayDateTime = () => {
        if(value === null){
            setError("Please enter a date and time")
        }else{
            console.log(value.$d)
        }
    }

    const container = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    //background: "#f7f7f7",
    fontFamily: "Arial",
    backgroundImage: `url(${background})`,
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat'
    };

    const form = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#fff",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
    borderRadius: "10px",
    padding: "40px",
    maxWidth: "400px",
    width: "100%",
    };

    const input = {
    width: "100%",
    height: "40px",
    padding: "8px",
    borderRadius: "5px",
    border: "none",
    marginBottom: "20px",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
    };

    const button = {
    background: "#2b70fe",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
    };

    const addButton = {
    background: "#2b70fe",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
    marginTop: "10px",
    };

    return(
        <div className="Profile Page" style={container}>
            <div style={form}>
                <h3 style={{ marginBottom: "20px", color: "#333" }}> Activity and calories burned calculator </h3>

                <select
                value={activity}
                onChange={(event) => {
                setActivity(event.target.value);
                }}
                style={input}
                >
                <option value="">Select Activity</option>
                <option value="running">Running</option>
                <option value="swimming">Swimming</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>
                <option value="cycling">Cycling</option>

                </select>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                    <input
                    placeholder="Enter time taken"
                    style={input}
                    onChange={(event) => {
                    setTime(event.target.value);
                    }}
                    />

                    <select
                    value={timeUnit}
                    onChange={(event) => {
                    setTimeUnit(event.target.value);
                    }}
                    style={input}
                    >
                    <option value="">Select Unit</option>
                    <option value="hours">Hours</option>
                    <option value="minutes">Minutes</option>
                    </select>
                </div>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                    <input
                    placeholder="Enter distance"
                    style={input}
                    onChange={(event) => {
                    setDistance(event.target.value);
                    }}
                    />

                    <select
                    value={distanceUnit}
                    onChange={(event) => {
                    setDistanceUnit(event.target.value);
                    }}
                    style={input}
                    >
                    <option value="">Select Unit</option>
                    <option value="metres">Metres</option>
                    <option value="kilometres">Kilometres</option>
                    <option value="miles">Miles</option>
                    </select>
                </div>
                <p style={{ color: "red",  }}>{dateTimeError}</p>

                <button onClick={displayValues} style={button}> Calculate </button>

                {showResult ? (
                    <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DemoContainer components={['DesktopDateTimePicker']}>
                    <DesktopDateTimePicker label="Enter Date and Time" value={value} onChange={(newValue) => setValue(newValue)} />
                    </DemoContainer>
                    <p style={{ color: "red",  }}>{error}</p>
                </LocalizationProvider>
                ) : null}

                {showResult ? (
                        <button onClick={displayDateTime} style={button}> Add to Activity Log </button>
                    ) : null}
            </div>
        </div>
    )
}
