import './App.css';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import ProfilePage from './ProfilePage';
import { BrowserRouter, Route, Routes, NavLink } from "react-router-dom";
import { Navigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { getAuth, onAuthStateChanged, signOut } from "firebase/auth";
import { useState, useEffect } from 'react';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setLoggedIn(true);
      } else {
        setLoggedIn(false);
      }
    });

    return () => unsubscribe();
  }, []);

  const SignOut = async () => {
    try {
      await signOut(auth)
      console.log("Successfully signed out");
      navigate("/login");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <BrowserRouter>

      <Navbar className='color-nav'  >
        <Container>
          <Navbar.Brand>FitnessPal</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {!loggedIn ? (
                <Nav.Item>
                  <Nav.Link as={NavLink} to="/login" activeclassname="active">Login</Nav.Link>
                </Nav.Item>
              ) : null}
              {!loggedIn ? (
                <Nav.Item>
                  <Nav.Link as={NavLink} to="/signup" activeclassname="active">Signup</Nav.Link>
                </Nav.Item>
              ) : null}
              {loggedIn ? (
                <Nav.Item>
                  <Nav.Link as={NavLink} to="/profile" activeclassname="active">Profile</Nav.Link>
                </Nav.Item>
              ) : null}
              {loggedIn ? (
                <Nav.Item>
                  <Nav.Link onClick={SignOut}>Sign Out</Nav.Link>
                </Nav.Item>
              ) : null}
            </Nav>
            </Navbar.Collapse>
        </Container>
      </Navbar>

      <Routes>
      <Route path='/login' element={<LoginPage/>} />          
      <Route path='/signup' element={<SignupPage />} />
      <Route path="/" element={<Navigate to="/login" replace />} />
      <Route path='/profile' element={<ProfilePage/>} />
      </Routes>

    </BrowserRouter>
  );
}

export default App;
