import './App.css';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import ProfilePage from './ProfilePage';
import { BrowserRouter, Route, Routes, NavLink } from "react-router-dom";
import { Navigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { getAuth, onAuthStateChanged } from "firebase/auth";

function App() {
  let loggedIn = true;
  const auth = getAuth();
  onAuthStateChanged(auth, (user) => {
    if (user) {
      // User is signed in, see docs for a list of available properties
      // https://firebase.google.com/docs/reference/js/firebase.User
      const uid = user.uid;
      loggedIn = true;
    } else {
      // User is signed out
      loggedIn = false;
    }
  });

  return (
    <BrowserRouter>

      <Navbar className='color-nav'  >
        <Container>
          <Navbar.Brand>FitnessPal</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Item>
                <Nav.Link as={NavLink} to="/login" activeclassname="active">Login</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link as={NavLink} to="/signup" activeclassname="active">Signup</Nav.Link>
              </Nav.Item>
              {loggedIn ? (
                <Nav.Item>
                  <Nav.Link as={NavLink} to="/profile" activeclassname="active">
                    Profile
                  </Nav.Link>
                </Nav.Item>
              ) : null}
            </Nav>
            </Navbar.Collapse>
        </Container>
      </Navbar>

      <Routes>
      <Route path='/login' element={<LoginPage/>} />          
      <Route path='/signup' element={<SignupPage />} />
      <Route path="/" element={<Navigate to="/login" replace />} />
      <Route path='/profile' element={<ProfilePage/>} />
      </Routes>

    </BrowserRouter>
  );
}

export default App;
