import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { doc, getDoc, updateDoc} from "firebase/firestore"
import { auth, db} from "./firebase-config";
import background from "./wallpaper.jpeg";

export default function CalculatorPage() {
    const userID = auth.currentUser.uid;
    const [activity, setActivity] = useState("");
    const [time, setTime] = useState(0);

    const container = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    //background: "#f7f7f7",
    fontFamily: "Arial",
    backgroundImage: `url(${background})`,
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat'
    };

    const form = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    backgroundColor: "#fff",
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)",
    borderRadius: "10px",
    padding: "40px",
    maxWidth: "400px",
    width: "100%",
    };

    const input = {
    width: "100%",
    height: "40px",
    padding: "8px",
    borderRadius: "5px",
    border: "none",
    marginBottom: "20px",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
    };

    const button = {
    background: "#2b70fe",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    transition: "all 0.3s ease-in-out",
    };

    return(
        <div className="Profile Page" style={container}>
            <div style={form}>
                <h3 style={{ marginBottom: "20px", color: "#333" }}> Activity and calories burned calculator </h3>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                <p style={{ marginRight: "10px" }}>First name</p>
                <input
                    placeholder={firstName}
                    style={input}
                />
                </div>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                <p style={{ marginRight: "10px" }}>Last name</p>
                <input
                    placeholder={lastName}
                    style={input}
                />
                </div>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                <p style={{ marginRight: "10px" }}>Weight</p>
                <input
                    placeholder="Enter Weight..."
                    value={weight}
                    style={input}
                    onChange={(event) => {
                        setWeight(event.target.value);
                    }}
                />
                </div>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                <p style={{ marginRight: "10px" }}>Height</p>
                <input
                    placeholder="Enter Height..."
                    value={height}
                    style={input}
                    onChange={(event) => {
                        setHeight(event.target.value);
                    }}
                />
                </div>

                <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>
                <p style={{ marginRight: "10px" }}>Age</p>
                <input
                    placeholder="Enter Age..."
                    value={age}
                    style={input}
                    onChange={(event) => {
                        setAge(event.target.value);
                    }}
                />
                </div>

                <button onClick={updateDetails} style={button}> Update </button>
            </div>
        </div>

    )
}
